pub enum LoopMode {
    RefreshSync,
    Rate {
        update_interval: Duration,
    },
    Wait,
    NTimes {
        number_of_updates: usize,
    },
}

pub enum Event {
    WindowEvent {
        id: window::Id,
        simple: Option<WindowEvent>,
    },
    DeviceEvent(winit::event::DeviceId, winit::event::DeviceEvent),
    Update(Update),
    Suspended,
    Resumed,
}

pub enum Break {
    Wrap {
        byte: usize,
        char: usize,
        len_bytes: usize,
        len_chars: usize,
    },
    Newline {
        byte: usize,
        char: usize,
        len_bytes: usize,
        len_chars: usize,
    },
    End {
        byte: usize,
        char: usize,
    },
}
